var spoiler=$(".spoiler");spoiler.click(function(i){i.preventDefault(),spoiler.not(this).siblings("div").animate({height:"hide"},500);var s=$(this).siblings("div");"none"==s.css("display")?($(".spoiler").html('<span>HIDE</span><img class="arrow_top" src="img/arrow.png" alt="arrow">'),s.animate({height:"show"},500)):($(".spoiler").html('<span>SHOW</span><img class="arrow_bottom" src="img/arrow.png" alt="arrow">'),s.animate({height:"hide"},500))});